    ai = TreePlayer("AI", wait=0)
    steps = []
    for _ in range(test_num):
        steps.append(run_game(ai, rnd=10))
    avg = sum(steps) / len(steps)
    logger.info(f"{ai} avg cost{avg:.3f} steps with {test_num} tests")


if __name__ == '__main__':
