class MineField:
    def __init__(self, size, n_bombs):
        self.size = size
        self.digged = 0
        self.n_bombs = n_bombs
        self.grid = [[0 for j in range(size)] for i in range(size)]
        self.grid_mask = [['Â·' for j in range(size)] for i in range(size)]
        self.plant_bombs()
        # self.assign_numbers()
        self.first_dig()

    def __str__(self):

        _str = '    ';

        for i in range(1, self.size+1):
            _str += str(i) + ' '
            if i < 10:
                _str += ' '
        _str += '\n'

        _str += ' ' + 'â€•' * (self.size * 3 + 3) + '\n'

        for i, row in enumerate(self.grid_mask, 1):
            if i < 10:
                _str += ' '
            _str += str(i) + '| '
            for char in row:
                _str += str(char) + '  '
            _str += '\n'
        return _str

    def plant_bombs(self):
        bombs_planted = 0
        while bombs_planted < self.n_bombs:
            row = randint(0, self.size - 1)
            column = randint(0, self.size - 1)

            if self.grid[row][column] == '*':
                continue

            self.grid[row][column] = '*'

            self.fill_neighbors(row, column)

            bombs_planted += 1

    def fill_neighbors(self, row, column):
            if row != 0:
                if column != 0:
                    if self.grid[row-1][column-1] != '*':
                        self.grid[row-1][column-1] += 1

                if self.grid[row-1][column] != '*':
                    self.grid[row-1][column] += 1

                if column != self.size-1:
                    if self.grid[row-1][column+1] != '*':
                        self.grid[row-1][column+1] += 1

            if column != 0:
                if self.grid[row][column-1] != '*':
                    self.grid[row][column-1] += 1

            if column != self.size-1:
                if self.grid[row][column+1] != '*':
                    self.grid[row][column+1] += 1

            if row != self.size-1:
                if column != 0:
                    if self.grid[row+1][column-1] != '*':
                        self.grid[row+1][column-1] += 1

                if self.grid[row+1][column] != '*':
                    self.grid[row+1][column] += 1

                if column != self.size-1:
                    if self.grid[row+1][column+1] != '*':
                        self.grid[row+1][column+1] += 1

    def first_dig(self):
        zeros = []
        for row in range(self.size):
            for column in range(self.size):
                if self.grid[row][column] == 0:
                    zeros.append((row, column))

        spot_to_dig = choice(zeros)
        self.dig(spot_to_dig[0], spot_to_dig[1])

    def dig(self, row, col):
        spot = self.grid[row][col]
        if spot == '*':
            return False
        elif spot != self.grid_mask[row][col]:
            self.grid_mask[row][col] = spot
            self.digged += 1
            if (spot == 0):
                self.clear_zeros(row, col)
            return True

    def clear_zeros(self, row, col):
        if row != 0:
            if col != 0:
                self.dig(row-1, col-1)

            self.dig(row-1, col)

            if col != self.size-1:
                self.dig(row-1, col+1)

        if col != 0:
            self.dig(row, col-1)

        if col != self.size-1:
            self.dig(row, col+1)

        if row != self.size-1:
            if col != 0:
                self.dig(row+1, col-1)

            self.dig(row+1, col)

            if col != self.size-1:
                self.dig(row+1, col+1)

    def mark_spot(self, row, col):
        spot_mask = self.grid_mask[row][col]
        if spot_mask == 'X':
            self.grid_mask[row][col] = 'Â·'
        elif spot_mask == 'Â·':
            self.grid_mask[row][col] = 'X'

    def show_bombs(self):
        for row in range(self.size):
            for column in range(self.size):
                if self.grid[row][column] == '*':
                    self.grid_mask[row][column] = '*'


