                ANSWERED = True
            if QUESTIONS_INDEX > len(datatable):
                END = True
                sg.popup(f'The end. You got {CORRECT} of {len(datatable)}.')
            if not END:
                window.write_event_value('-START-', '')

    if event in (sg.WIN_CLOSED, '-EXIT-'):
        break


window.close()

